  {"title"=>"Nootdorp Skatepark", "description"=>"Komplett aus Beton, Bowl und Street. Bowl ist hammer, Street geht so, die schrägen Stahlwände sind nice.", "type"=>"Bowl", "lat"=>52.0419, "lng"=>4.39331, "slug"=>"nootdorp-skatepark", "obstacles"=>[]},
